<?php $to = 'info@visioncapitalgf.com';if (isset($_POST['sendMail'])) {    if ($_POST['name']) {        $name = 'Name: ' . $_POST['name'] . "\r\n\r\n";    }    if ($_POST['email']) {        $email = $_POST['email'];        $email_mess = 'E-mail: ' . $_POST['email'] . "\r\n\r\n";    }    $message = '';    if ($_POST['message']) {        $message = 'Message: ' . $_POST['message'] . "\r\n\r\n";    }    if (isset($_POST['ip'])) {        $message .= 'IP: ' . $_POST['ip'] . "\r\n\r\n";    }    if (isset($_POST['ref'])) {        $message .= 'Ref: ' . $_POST['ref'] . "\r\n\r\n";    }    if ($_POST['subject']) {        $subject_tem = 'Subject: ' . $_POST['subject'] . "\r\n\r\n";    } else {        $subject_tem = 'Feedback' . "\r\n\r\n";    }    $body = $subject_tem . $name . $email_mess . $message;    send_mail($to, $body, $email);    print_r($body);}function send_mail($to, $body, $email){    $subject = 'Feedback ' . $_SERVER['HTTP_HOST'];    $boundary = "--" . md5(uniqid(time()));    $headers = "From: noreply@" . $_SERVER['HTTP_HOST'] . "\r\n";    $headers .= "MIME-Version: 1.0\r\n";    $headers .= "Content-Type: multipart/mixed; boundary=\"" . $boundary . "\"\r\n";    $multipart = "--" . $boundary . "\r\n";    $multipart .= "Content-type: text/plain; charset=\"utf-8\"\r\n";    $multipart .= "Content-Transfer-Encoding: quoted-printable\r\n\r\n";    $body = $body . "\r\n\r\n";    $multipart .= $body;    mail($to, $subject, $multipart, $headers);} ?>